import { APIGatewayEvent, APIGatewayProxyEvent, Context } from 'aws-lambda'
import { SignToken } from '.'

export type JsonObject = { [Key in string]?: JsonValue }
export type JsonArray = Array<JsonValue>
export type JsonValue = string | number | boolean | null | JsonObject | JsonArray | any;

export interface APIGatewayEventInterface extends APIGatewayProxyEvent {
  jsonBody?: JsonValue
  currentUser?: SignToken
}
